s=\"a2a_dd\" href=\"//www.addtoany.com/share_save?linkurl=http%3A%2F%2Fallafrica.com%2Fstories%2F201601020101.html&amp;linkname=No%20Ban%20On%20Hijab%20By%20Muslim%20Women%20-%20Presidency\">\n           <div class=\"share-button more\">\n            <span class=\"icon\">\n            </span>\n            <span class=\"action\">\n             more\n            </span>\n           </div>\n          </a>\n         </div>\n         <div style=\"clear: both;\">\n         </div>\n         <script type=\"text/javascript\">\n          function my_addtoany_onready() {\na2a.init('page', {\ntarget: '.share-button.more',\nlinkname: \"No Ban On Hijab By Muslim Women - Presidency\",\nshow_title: 1,\nlinkurl: 'http://allafrica.com/stories/201601020101.html',\n});\na2a.getByClass(\"sharebar solid\",document)[0].style.display = 'block';\n}\n// Setup Lockerz Share \"onReady\" callback\nvar a2a_config = a2a_config || {};\na2a_config.tracking_callback = {\nready: my_addtoany_onready,\n};\na2a_config.onclick = true;\na2a_config.num_services = 8;\na2a_config.hide_embeds = 1;\na2a_config.orientation = 'up';\na2a_config.prioritize = [\"facebook\", \"twitter\", \"google_plus\", \"pinterest\", \"linkedin\", \"reddit\", \"digg\", \"delicious\", \"stumbleupon\"];\na2a_config.locale = \"en\";\n// Load Lockerz Share script asynchronously\n_ready.push(function($) {\nvar a = document.createElement('script');\na.type = 'text/javascript';\na.async = true;\na.src = '//static.addtoany.com/menu/page.js';\nvar s = document.getElementsByTagName('script')[0];\ns.parentNode.insertBefore(a, s);\n});\n         </script>\n        </div>\n        <!--/.sharebar.solid-->\n        <div class=\"useful-top\">\n         <div class=\"column useful\">\n          <div class=\"content\">\n          </div>\n          <!--/.content-->\n         </div>\n         <!--/.column.useful-->\n        </div>\n        <!--/.useful-top-->\n        <div class=\"content-container\">\n         <div class=\"story-body\">\n          <p class=\"story-body-text\" data-para-word-count=\"23\">\n           The Presidency has dismissed the insinuations that the Federal Government had banned the use of Hijab, the Muslim women's dressing symbol of modesty.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"26\">\n           This is contained in a statement issued in Abuja on Friday by the Senior Special Assistant on Media and Publicity to the President, Malam Garba Shehu.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"25\">\n           Giving a clarification on the pronouncement of the President on the issue during last Wednesday's Presidential Media Chat, Shehu said that the speculations were unfounded.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"22\">\n           He said that the Buhari-led Federal Government would always respect the rights of Muslims to protect their modesty and allow religious freedom.\n          </p>\n          <div id=\"in-article-p-mid\">\n          </div>\n          <div id=\"aans-body-mid\">\n          </div>\n          <p class=\"story-body-text\" data-para-word-count=\"24\">\n           Shehu said that the Hijab was being abused by terrorists to carry out suicide attacks on innocent people, which violated the teachings of Islam.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"20\">\n           He, however, said that Buhari would not take any decision on the issue without due consultation with all the stakeholders.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"23\">\n           He said that the government would address the security challenge in consultations with Muslim leaders with a view to finding a workable solution.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"26\">\n           \"Everything will be done to balance national security requirements with the rights and obligations of citizens under their religions as protected by the constitution,\" he said.\n          </p>\n          <div id=\"aans-body-end\">\n          </div>\n         </div>\n         <!--/.story-body-->\n        </div>\n        <!--./content-container-->\n        <div style=\"clear: both;\">\n        </div>\n        <div id=\"in-article-bottom\">\n        </div>\n        <div style=\"clear: both;\">\n        </div>\n        <div class=\"outer-ad-container responsive-aans-view-end-container\" id=\"responsive-aans-view-end-container\">\n         <!-- google ad container for 'AllAfrica_ResponsiveAansViewEnd'-->\n         <div class=\"ad-container google-ad google-responsive-ad responsive-ad responsive-aans-view-ad responsive-aans-view-end\">\n          <ins class=\"adsbygoogle\" data-ad-client=\"ca-pub-2420009840005975\" data-ad-slot=\"6855956821\" style=\"display:block\">\n          </ins>\n          <script>\n           (adsbygoogle = window.adsbygoogle || []).push({});\n          </script>\n         </div>\n         <!--/.ad-container.google-ad.google-responsive-ad.responsive-ad.responsive-aans-view-ad.responsive-aans-view-end-->\n        </div>\n        <!--/.outer-ad-container.responsive-aans-view-end-container-->\n        <div style=\"clear: both;\">\n        </div>\n        <ul class=\"sharebar standard\">\n         <li class=\"facebook\">\n          <div class=\"fb-like\" data-action=\"recommend\" data-colorscheme=\"light\" data-href=\"http://allafrica.com/stories/201601020101.html\" data-layout=\"button_count\" data-share=\"false\" data-show-faces=\"false\">\n          </div>\n         </li>\n         <li class=\"twitter\">\n          <a class=\"twitter-share-button\" data-count=\"horizontal\" data-counturl=\"http://allafrica.com/stories/201601020101.html\" data-hashtags=\"nigeria\" data-related=\"allafrica, allafricafrench\" data-size=\"medium\" data-text=\"No Ban On Hijab By Muslim Women - Presidency\" data-url=\"http://allafrica.com/stories/201601020101.html\" data-via