rz Share script asynchronously\n_ready.push(function($) {\nvar a = document.createElement('script');\na.type = 'text/javascript';\na.async = true;\na.src = '//static.addtoany.com/menu/page.js';\nvar s = document.getElementsByTagName('script')[0];\ns.parentNode.insertBefore(a, s);\n});\n         </script>\n        </div>\n        <!--/.sharebar.solid-->\n        <div class=\"useful-top\">\n         <div class=\"column useful\">\n          <div class=\"content\">\n          </div>\n          <!--/.content-->\n         </div>\n         <!--/.column.useful-->\n        </div>\n        <!--/.useful-top-->\n        <div class=\"content-container\">\n         <div class=\"story-body\">\n          <div class=\"figure resource inset left\">\n           <div class=\"video-container\">\n            <iframe allowfullscreen=\"\" class=\"youtube-player\" frameborder=\"0\" height=\"160\" src=\"//www.youtube.com/embed/Qrdjsr8DDhc?controls=1&amp;showinfo=0\" type=\"text/html\" width=\"285\">\n            </iframe>\n           </div>\n           <div class=\"figcaption\">\n            <span class=\"credit\">\n             <a href=\"/view/resource/main/main/id/00100211.html\">\n              RESOURCE: Al-Shabaab Uses Donald Trump in Video Aimed at Western Muslims\n             </a>\n            </span>\n           </div>\n          </div>\n          <p class=\"story-body-text\" data-para-word-count=\"34\">\n           <em>\n            Somalian terror group, al-Shabab, has used clips of US presidential candidate Donald Trump in its propaganda film. Trump courted controversy in December when he demanded that all Muslims be barred from entering the US.\n           </em>\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"15\">\n           The al-Shabab video was distributed on Twitter by the al-Kataib Media Foundation, an Islamist organization.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"46\">\n           The terrorists used a clip from Trump's December speech, where he made calls for the United States to bar Muslims from entering the country as his supporters cheered. Many American politicians condemned Trump's statement and said his views could be used as propaganda by militant organizations.\n          </p>\n          <div style=\"clear: both;\">\n          </div>\n          <div class=\"outer-ad-container responsive-aans-view-a-container\" id=\"responsive-aans-view-a-container\">\n           <!-- google ad container for 'AllAfrica_ResponsiveAansViewA'-->\n           <div class=\"ad-container google-ad google-responsive-ad responsive-ad responsive-aans-view-ad responsive-aans-view-a\">\n            <ins class=\"adsbygoogle\" data-ad-client=\"ca-pub-2420009840005975\" data-ad-slot=\"3319981629\" style=\"display:block\">\n            </ins>\n            <script>\n             (adsbygoogle = window.adsbygoogle || []).push({});\n            </script>\n           </div>\n           <!--/.ad-container.google-ad.google-responsive-ad.responsive-ad.responsive-aans-view-ad.responsive-aans-view-a-->\n          </div>\n          <!--/.outer-ad-container.responsive-aans-view-a-container-->\n          <p class=\"story-body-text\" data-para-word-count=\"57\">\n           The al-Shabab seemed to have done exactly that. Trump's clip in the 51-minute-long film was sandwiched between clips of militant leader Anwar al-Awlaki saying that Muslims would face a choice between leaving for Islamic countries or staying at home to fight the West, SITE monitoring reported. Al-Awlaki was killed in a drone strike in Yemen in 2011.\n          </p>\n          <div id=\"in-article-p-mid\">\n          </div>\n          <div id=\"aans-body-mid\">\n          </div>\n          <p class=\"story-body-text\" data-para-word-count=\"34\">\n           The video, confirmed to be genuine by experts, has footage of the US police using force against black people as well as speeches of the African-American activist Malcolm X, who was killed in 1965.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"35\">\n           The al-Shabab, an affiliate of the al-Qaida, seeks to overthrow Somalia's government and impose an Islamic regime following a strict version of the Sharia. The organization has carried out many attacks in Kenya and Ethiopia.\n          </p>\n          <p class=\"story-body-text\" data-para-word-count=\"3\">\n           <em>\n            mg/se (Reuters, dpa)\n           </em>\n          </p>\n          <div id=\"aans-body-end\">\n          </div>\n         </div>\n         <!--/.story-body-->\n         <div style=\"clear: both;\">\n         </div>\n         <div style=\"clear: both;\">\n         </div>\n         <div style=\"width: 100%; text-align: center; margin-bottom: 16px;\">\n          <iframe frameborder=\"no\" height=\"185\" scrolling=\"no\" src=\"http://partner.dw.de/syndication/feeds/en-VAF-CB-AfricaLink-audio-allAfrica.14019-cb.html\" style=\"margin: 0 auto;\" width=\"322\">\n          </iframe>\n         </div>\n         <script type=\"text/javascript\">\n          <!--\n//DW Tracking start\nvar xttitel = encodeURIComponent (document.title); // Pagetitel\nvar partnername = encodeURIComponent('allafrica') ; // Partner\nvar sprache = '2'; // Language\nvar xtmaca = 'eng-VAF-Volltexte-JavaScript-' + partnername + '-dwde'; //maca Parameter\nvar xturl = encodeURIComponent (document.URL); // URL\nvar xtref = encodeURIComponent (document.referrer); // referrer\nvar base = 'http://logc279.xiti.com/hit.xiti?s=531599&amp;s2=' + sprache + '&amp;p=' + partnername + '::Volltexte::'; // First request part\nvar ext = '&amp;x1=1&amp;x2=' + sprache + '&amp;x5=' + xttitel + '&amp;x6=1&amp;x7=' + xturl + '&amp;x8=' + xtmaca + '&amp;x10=' + partnername + '::Volltexte&amp;ref=' + xtref; //second request part\nvar track = base + xttitel + ext; // Request\nvar xtImg=document.createElement(\"img\");\nxtImg.setAttribute('src', track);\nxtImg.setAttribute('alt', 'na');